# print all argumetns
def allargs(parg, *args, name=1, **kwargs): 
     print('first positional arg:', parg) 
     for arg in args: 
         print('arg from *argv:', arg) 
     print('first keyword arg name:', name) 
     for key, value in kwargs.items(): 
         print('kwarg: {0} = {1}'.format(key, value))
		 
# Behaves similarly to functools.partial
# https://docs.python.org/3/library/functools.html
# except supplied arguments to the partial override
# any passed arguments during partial call
def partial(func, *args, **keywords):
    def newfunc(*fargs, **fkeywords):
        newkeywords = keywords.copy()
        # newkeywords.update(fkeywords)
        fkeywords.update(newkeywords)
        return func(*args, *fargs, **fkeywords)
    newfunc.func = func
    newfunc.args = args
    newfunc.keywords = keywords
    return newfunc